OP.io = io.connect();

OP.Subjectification.load = function(jsonClassMap, cb) {
    var nrem = 0;

    for(var key in jsonClassMap) {
        nrem += 1;
        var cls = jsonClassMap[key];
        OP.io.emit("load", key, function(res) {
            nrem -= 1;
            for(var id in res) {
                var obj = new cls(id, res[id]);
                obj.save();
            }
            if(nrem == 0) {
                cb();
            }
        });
    }
};
